import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Pagination from "../common/Pagination";
import ConfirmDialog from "../common/ConfirmDialog";
import { fetchGrup } from "../../services";

export default function Grup({ history }) {
  const { grupId } = useParams();
  const [grup, setGrup] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [search, setSearch] = useState("");
  const [ratingFilter, setRatingFilter] = useState(0);
  const [total, setTotal] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [page, setPage] = useState(1);
  const [pending, setPending] = useState(false);
  const [submitReviewError, setSubmitReviewError] = useState(null);
  const [replyReviewId, setReplyReviewId] = useState(0);
  const [submitReplyError, setSubmitReplyError] = useState(null);
  const [confirmDialogInfo, setConfirmDialogInfo] = useState({
    open: false,
    action: null,
    id: null,
  });

  async function fetchGrupData() {
    if (!grupId) {
      history.goBack();
    }
    try {
      const data = await fetchGrup(grupId);
      setGrup(data);
      console.log(data);
    } catch (e) {
      history.goBack();
    }
  }

  useEffect(() => {
    fetchGrupData();
  }, []);

  useEffect(() => {
    fetchReviewsData();
  }, [page, perPage, search, GrupId, pending]);

  const onHideConfirmDialog = () => {
    setConfirmDialogInfo({
      open: false,
    });
  };

  const onDialogConfirm = async () => {
    try {
      if (confirmDialogInfo.action === "delete_review") {
        await deleteReview(confirmDialogInfo.id);
      } else if (confirmDialogInfo.action === "delete_reply") {
        await deleteReply(confirmDialogInfo.id);
      }
      fetchReviewsData();
      fetchGrupData();
    } catch (e) {
      console.log(e);
      fetchReviewsData();
    }

    setConfirmDialogInfo({
      open: false,
    });
  };

  const renderReviews = () => {
    console.log(reviews);
    return (
      <div className="review-list">
        {reviews &&
          reviews.map((review) => (
            <div className="review-item pb-4 pt-2" key={review.id}>
              <Review
                review={review}
                editable={editable}
                onUpdated={onUpdateReview}
                onDelete={onClickDeleteReview}
              />
              {review.reply && (
                <Reply
                  review={review}
                  editable={editable}
                  onUpdated={onUpdateReview}
                  onDelete={onClickDeleteReply}
                />
              )}
              {authRole === Role.Owner && (
                <>
                  {replyReviewId !== review.id && !review.reply && (
                    <a
                      className="small font-weight-bold text-dark"
                      href="#"
                      onClick={(e) => {
                        e.preventDefault();
                        onExpandReply(review.id);
                      }}
                    >
                      Reply
                    </a>
                  )}

                  {replyReviewId === review.id && (
                    <ReplyForm
                      onSubmit={(data) => onReply(review.id, data)}
                      onCancel={onCancelReply}
                      error={submitReplyError}
                    />
                  )}
                </>
              )}
            </div>
          ))}
      </div>
    );
  };
  const pageCount = Math.floor(total / perPage) + 1;
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-12">
          {grup && (
            <div className="card">
              <div className="card-body">
                <h2 className="mb-0">{grup.name}</h2>
                <div className="mb-4">
                  {grup.rating && (
                    <div>
                      <FiveStarRatings rating={grup.rating || 0} />{" "}
                      <span
                        className="text-secondary ml-2 font-weight-bold"
                        style={{ position: "relative", top: "5px" }}
                      >
                        {grup.rating.toFixed(1)}
                      </span>
                    </div>
                  )}
                </div>

                <p className="pt-2 mb-3">{grup.description}</p>
                <p className="mb-1">
                  <span>Location: </span>
                  <span className="text-secondary">{grup.location}</span>
                </p>
                {grup.highestReview && <hr className="mt-5" />}

                {grup.highestReview && (
                  <div>
                    <h6 className="mt-3 mb-1">Highest rated review</h6>
                    <Review review={grup.highestReview} />
                  </div>
                )}
                {grup.highestReview && (
                  <div>
                    <h6 className="mt-5 mb-1">Lowest rated review</h6>
                    <Review review={grup.lowestReview} />
                  </div>
                )}
              </div>
            </div>
          )}
          {grup && authRole === Role.Regular && !grup.reviewed && (
            <div className="card mt-5">
              <div className="card-body">
                <h5>Leave your review</h5>
                <ReviewForm
                  onSubmit={onSubmitReview}
                  error={submitReviewError}
                />
              </div>
            </div>
          )}
          {loaded && (
            <div className="card mt-5">
              <div className="card-body">
                <h5 className="mt-2">
                  Reviews <span className="text-secondary">({total})</span>
                </h5>
                <div className="my-3">
                  <span className="mr-3">Filter by</span>{" "}
                  <StarRatingDropdown
                    value={ratingFilter}
                    onChange={setRatingFilter}
                  />
                  {authRole === Role.Owner && (
                    <>
                      <span className="ml-4 mr-2">Pending Reviews</span>{" "}
                      <input
                        type="checkbox"
                        value={pending}
                        onChange={() => setPending(!pending)}
                      />
                    </>
                  )}
                </div>

                {renderReviews()}
              </div>
              <div className="mr-5">
                <div className="float-right"></div>
                <Pagination
                  perPage={perPage}
                  page={page}
                  onChange={(p) => setPage(p)}
                  total={total}
                />
              </div>
            </div>
          )}

          {fetchError && <p className="text-danger mt-4">{fetchError}</p>}
        </div>
        <ConfirmDialog
          show={confirmDialogInfo.open}
          text={confirmDialogInfo.text}
          onConfirm={onDialogConfirm}
          onHide={onHideConfirmDialog}
        />
      </div>
    </div>
  );
}
