import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import ConfirmDialog from "../common/ConfirmDialog";
import Button from "react-bootstrap/Button";
import { fetchGrup } from "../../services";
import moment from "moment-timezone";
import { Link, history } from "react-router-dom";

export default function Grup({ history }) {
  const { grupId } = useParams();
  const [grup, setGrup] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [search, setSearch] = useState("");
  const [total, setTotal] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [page, setPage] = useState(1);
  const [confirmDialogInfo, setConfirmDialogInfo] = useState({
    open: false,
    action: null,
    id: null,
  });

  async function fetchGrupData() {
    if (!grupId) {
      history.goBack();
    }
    try {
      const data = await fetchGrup(grupId);
      setGrup(data);
      console.log(data);
    } catch (e) {
      history.goBack();
    }
  }

  useEffect(() => {
    fetchGrupData();
  }, []);

  const onHideConfirmDialog = () => {
    setConfirmDialogInfo({
      open: false,
    });
  };

  const renderTable = () => {
    const users = grup.users;
    return (
      <table className="table table-hover mb-0">
        <thead>
          <tr>
            <th>Name</th>
            <th>Surname</th>

            <th>Created Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {grup.users &&
            grup.users.map((users, idx) => (
              <tr key={users._id}>
                <td>{users.name}</td>
                <td>{users.surname}</td>
                <td>{moment(users.createdAt).format("MMMM D YYYY")}</td>

                <td>
                  {users._id && (
                    <>
                      <Link
                        className="btn text-secondary"
                        to={`/userss/${users.id}/edit`}
                        onClick={(e) => e.stopPropagation()}
                      >
                        <i className="fas fa-edit"></i>
                      </Link>
                      <a className="btn text-secondary" onClick={(e) => {}}>
                        <i className="far fa-trash-alt"></i>
                      </a>
                    </>
                  )}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    );
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-12">
          {grup && (
            <div className="card">
              <div className="card-body">
                <h2 className="mb-0">{grup.name}</h2>

                <p className="pt-2 mb-3">{grup.description}</p>
              </div>
            </div>
          )}

          <div className="">{renderTable()}</div>

          {grup.users.length === 0 && (
            <div className="text-center">"No Users found."</div>
          )}

          {fetchError && <p className="text-danger mt-4">{fetchError}</p>}
        </div>
        <ConfirmDialog
          show={confirmDialogInfo.open}
          text={confirmDialogInfo.text}
          onHide={onHideConfirmDialog}
        />
      </div>
    </div>
  );
}
