import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Pagination from "../common/Pagination";
import ConfirmDialog from "../common/ConfirmDialog";
import { fetchGrup } from "../../services";
import { WithContext as ReactTags } from "react-tag-input";

export default function Grup({ history }) {
  const { grupId } = useParams();
  const [grup, setGrup] = useState(null);

  const [tags, setTags] = useState([
    { id: "Thailand", text: "Thailand" },
    { id: "India", text: "India" },
  ]);
  const [suggestions, setSuggestions] = useState([
    { id: "USA", text: "USA" },
    { id: "Germany", text: "Germany" },
    { id: "Austria", text: "Austria" },
    { id: "Costa Rica", text: "Costa Rica" },
    { id: "Sri Lanka", text: "Sri Lanka" },
    { id: "Thailand", text: "Thailand" },
  ]);
  const [reviews, setReviews] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [search, setSearch] = useState("");
  const [total, setTotal] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [page, setPage] = useState(1);
  const [confirmDialogInfo, setConfirmDialogInfo] = useState({
    open: false,
    action: null,
    id: null,
  });

  async function fetchGrupData() {
    if (!grupId) {
      history.goBack();
    }
    try {
      const data = await fetchGrup(grupId);
      setGrup(data);
      console.log(data);
    } catch (e) {
      history.goBack();
    }
  }

  useEffect(() => {
    fetchGrupData();
  }, []);

  const onHideConfirmDialog = () => {
    setConfirmDialogInfo({
      open: false,
    });
  };

  const handleDelete = (i) => {
    const { tags } = tags;
    this.setTags({
      tags: tags.filter((tag, index) => index !== i),
    });
  };
  const handleAddition = (tag) => {
    setTags([...tags, tag]);
  };

  const handleDrag = (tag, currPos, newPos) => {
    const tags = tags;
    const newTags = tags.slice();

    newTags.splice(currPos, 1);
    newTags.splice(newPos, 0, tag);

    // re-render
    this.setState({ tags: newTags });
    setTags(newTags);
  };

  const pageCount = Math.floor(total / perPage) + 1;
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-12">
          {grup && (
            <div className="card">
              <div className="card-body">
                <h2 className="mb-0">{grup.name}</h2>

                <p className="pt-2 mb-3">{grup.description}</p>
              </div>
            </div>
          )}

          <div className="card mt-5">
            <div className="card-body">
              <h5>Leave your review</h5>
            </div>
          </div>

          <div className="card mt-5">
            <div className="card-body">
              <h5 className="mt-2">
                Reviews <span className="text-secondary">({total})</span>
              </h5>
              <div className="my-3">
                <span className="mr-3">Filter by</span>{" "}
              </div>
            </div>
            <div className="mr-5">
              <div className="float-right"></div>
              <Pagination
                perPage={perPage}
                page={page}
                onChange={(p) => setPage(p)}
                total={total}
              />
            </div>
          </div>
          <div>
            <ReactTags
              tags={tags}
              suggestions={suggestions}
              handleDelete={this.handleDelete}
              handleAddition={this.handleAddition}
              handleDrag={this.handleDrag}
              delimiters={delimiters}
            />
          </div>

          {fetchError && <p className="text-danger mt-4">{fetchError}</p>}
        </div>
        <ConfirmDialog
          show={confirmDialogInfo.open}
          text={confirmDialogInfo.text}
          onHide={onHideConfirmDialog}
        />
      </div>
    </div>
  );
}
