import React, { useState, useEffect } from "react";
import { fetchGrups } from "../../services";
import Pagination from "../common/Pagination";

export default function Grups({ history }) {
  const [grups, setGrups] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [query, setQuery] = useState("");
  const [total, setTotal] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [page, setPage] = useState(1);
  const [ratingFilter, setRatingFilter] = useState(0);

  async function fetchData() {
    try {
      const data = await fetchGrups({
        page,
        perPage,
        query,
        rating: ratingFilter,
      });
      setGrups(data.grups);
      setTotal(data.total);
      setPage(data.page);
      setLoaded(true);
    } catch (e) {
      setFetchError(e.message);
    }
  }

  useEffect(() => {
    fetchData();
  }, [page, perPage, query, ratingFilter]);

  const onClickGrup = (grupId) => {
    history.push(`/grups/${grupId}`);
  };

  const pageCount = Math.floor(total / perPage) + 1;

  const renderTable = () => {
    return (
      <div className="row grup-list">
        {grups &&
          grups.map((grup, idx) => (
            <div
              className="grup-item cursor-pointer col-12 border my-1 py-3"
              key={grup.id}
              onClick={() => onClickGrups(grup.id)}
            >
              <h6 className="my-0">{grup.name}</h6>

              <div className="text-secondary">{grup.description}</div>
            </div>
          ))}
      </div>
    );
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-12">
          <div>
            <h4 className="pt-2">All Grups</h4>
            <div className="clearfix pt-3">
              <div className=" float-left form-group has-search mr-4">
                <span className="fa fa-search form-control-feedback"></span>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search"
                  value={query}
                  onChange={(e) => {
                    setQuery(e.target.value);
                    setPage(1);
                  }}
                />
              </div>
            </div>
          </div>
          <div className="">
            <div className="">{renderTable()}</div>

            {!fetchError && loaded && grups.length === 0 && (
              <div className="">No grups found</div>
            )}
          </div>

          <div className="mt-4">
            <div className="float-right">
              <Pagination
                perPage={perPage}
                page={page}
                total={total}
                onChange={(p) => setPage(p)}
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
