import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Pagination from "../common/Pagination";
import Dropdown from "../common/Dropdown";

import { fetchGrups } from "../../services";

export default function Grup({ history }) {
  const { GrupId } = useParams();
  const [Grup, setGrup] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [search, setSearch] = useState("");
  const [total, setTotal] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [page, setPage] = useState(1);
  const [pending, setPending] = useState(false);
  const [submitReviewError, setSubmitReviewError] = useState(null);
  const [replyReviewId, setReplyReviewId] = useState(0);
  const [submitReplyError, setSubmitReplyError] = useState(null);
  const [confirmDialogInfo, setConfirmDialogInfo] = useState({
    open: false,
    action: null,
    id: null,
  });

  async function fetchGrupData() {
    if (!GrupId) {
      history.goBack();
    }
    try {
      const data = await fetchGrup(GrupId);
      setGrup(data);
      console.log(data);
    } catch (e) {
      history.goBack();
    }
  }

  async function fetchUsersData() {
    try {
      const data = await fetchUsersForGrup(GrupId, {
        page,
        perPage,
        search,
      });

      setTotal(data.total);
      setPage(data.page);
      setLoaded(true);
    } catch (e) {
      setFetchError(e.message);
    }
  }

  useEffect(() => {
    fetchGrupData();
  }, []);

  useEffect(() => {
    fetchUsersData();
  }, [page, perPage, search, GrupId]);

  const onSubmitReview = async (data) => {
    try {
      await createReview(GrupId, data);
      fetchUsersData();
      fetchGrupData();
    } catch (e) {
      setSubmitReviewError(e.message);
    }
  };

  const onExpandReply = (reviewId) => {
    setReplyReviewId(reviewId);
  };

  const onUpdateReview = (updated) => {
    fetchUsersData();
    fetchGrupData();
  };
  const onReply = async (reviewId, replyData) => {
    try {
      const data = await replyToReview(reviewId, replyData);
      const newReviews = reviews.map((review) =>
        review.id === reviewId ? data : review
      );
      setReviews(newReviews);
      setReplyReviewId(0);
    } catch (e) {
      setSubmitReplyError(e.message);
    }
  };

  const onClickDeleteReview = (reviewId) => {
    setConfirmDialogInfo({
      open: true,
      text: "Are you sure to delete this review?",
      action: "delete_review",
      id: reviewId,
    });
  };

  const onHideConfirmDialog = () => {
    setConfirmDialogInfo({
      open: false,
    });
  };

  const onDialogConfirm = async () => {
    try {
      if (confirmDialogInfo.action === "delete_review") {
        await deleteReview(confirmDialogInfo.id);
      } else if (confirmDialogInfo.action === "delete_reply") {
        await deleteReply(confirmDialogInfo.id);
      }
      fetchUsersData();
      fetchGrupData();
    } catch (e) {
      console.log(e);
      fetchUsersData();
    }

    setConfirmDialogInfo({
      open: false,
    });
  };

  const renderReviews = () => {
    console.log(reviews);
    return <div className="review-list"></div>;
  };
  const pageCount = Math.floor(total / perPage) + 1;
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-12">
          {Grup && (
            <div className="card">
              <div className="card-body">
                <h2 className="mb-0">{Grup.name}</h2>
                <div className="mb-4">
                  {Grup.rating && (
                    <div>
                      <Fives rating={Grup.rating || 0} />{" "}
                      <span
                        className="text-secondary ml-2 font-weight-bold"
                        style={{ position: "relative", top: "5px" }}
                      >
                        {Grup.rating.toFixed(1)}
                      </span>
                    </div>
                  )}
                </div>

                <p className="pt-2 mb-3">{Grup.description}</p>
                <p className="mb-1">
                  <span>Location: </span>
                  <span className="text-secondary">{Grup.location}</span>
                </p>
                {Grup.highestReview && <hr className="mt-5" />}

                {Grup.highestReview && (
                  <div>
                    <h6 className="mt-3 mb-1">Highest rated review</h6>
                    <Review review={Grup.highestReview} />
                  </div>
                )}
                {Grup.highestReview && (
                  <div>
                    <h6 className="mt-5 mb-1">Lowest rated review</h6>
                    <Review review={Grup.lowestReview} />
                  </div>
                )}
              </div>
            </div>
          )}
          {Grup && (
            <div className="card mt-5">
              <div className="card-body">
                <h5>Leave your review</h5>
                <ReviewForm
                  onSubmit={onSubmitReview}
                  error={submitReviewError}
                />
              </div>
            </div>
          )}
          {loaded && (
            <div className="card mt-5">
              <div className="card-body">
                <h5 className="mt-2">
                  Reviews <span className="text-secondary">({total})</span>
                </h5>
                <div className="my-3">
                  <span className="mr-3">Filter by</span>{" "}
                  <Dropdown value={ratingFilter} onChange={setRatingFilter} />
                  <>
                    <span className="ml-4 mr-2">Pending Reviews</span>{" "}
                    <input
                      type="checkbox"
                      value={pending}
                      onChange={() => setPending(!pending)}
                    />
                  </>
                </div>

                {renderReviews()}
              </div>
              <div className="mr-5">
                <div className="float-right"></div>
                <Pagination
                  perPage={perPage}
                  page={page}
                  onChange={(p) => setPage(p)}
                  total={total}
                />
              </div>
            </div>
          )}

          {fetchError && <p className="text-danger mt-4">{fetchError}</p>}
        </div>
        <ConfirmDialog
          show={confirmDialogInfo.open}
          text={confirmDialogInfo.text}
          onConfirm={onDialogConfirm}
          onHide={onHideConfirmDialog}
        />
      </div>
    </div>
  );
}
