import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import ConfirmDialog from "../common/ConfirmDialog";
import Button from "react-bootstrap/Button";
import { fetchGrup } from "../../services";

export default function Grup({ history }) {
  const { grupId } = useParams();
  const [grup, setGrup] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [search, setSearch] = useState("");
  const [total, setTotal] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [page, setPage] = useState(1);
  const [confirmDialogInfo, setConfirmDialogInfo] = useState({
    open: false,
    action: null,
    id: null,
  });

  async function fetchGrupData() {
    if (!grupId) {
      history.goBack();
    }
    try {
      const data = await fetchGrup(grupId);
      setGrup(data);
      console.log(data);
    } catch (e) {
      history.goBack();
    }
  }

  useEffect(() => {
    fetchGrupData();
  }, []);

  const onHideConfirmDialog = () => {
    setConfirmDialogInfo({
      open: false,
    });
  };

  const pageCount = Math.floor(total / perPage) + 1;
  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-12">
          {grup && (
            <div className="card">
              <div className="card-body">
                <h2 className="mb-0">{grup.name}</h2>

                <p className="pt-2 mb-3">{grup.description}</p>
              </div>
            </div>
          )}

          <div className="card mt-5">
            <div className="card-body">
              <h5>Users </h5>
            </div>
          </div>

          <div className="card mt-5">
            <div className="card-body ">
              qqqw
              <Button className="align-items-end" variant="danger">
                Danger
              </Button>
            </div>
          </div>

          {fetchError && <p className="text-danger mt-4">{fetchError}</p>}
        </div>
        <ConfirmDialog
          show={confirmDialogInfo.open}
          text={confirmDialogInfo.text}
          onHide={onHideConfirmDialog}
        />
      </div>
    </div>
  );
}
